<?phpclass WeixinController extends Controller {	public $layout = 'clean';	public function actions() {		return array(			// captcha action renders the CAPTCHA image displayed on the contact page			'captcha' => array(				'class' => 'CCaptchaAction',				'backColor' => 0xFFFFFF,			),			// page action renders "static" pages stored under 'protected/views/site/pages'			// They can be accessed via: index.php?r=site/page&view=FileName			'page' => array(				'class' => 'CViewAction',			),		);	}	public function actionAuth() {		$this->layout = "clean";		$this->render('auth');	}	public function actionOauth() {		$this->layout = "clean";		$this->render('oauth');	}}?>